import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class CreatePurchase extends Component {
  constructor(props) {
    super(props);

    this.onChangeCartName = this.onChangeCartName.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      cartName: '',
      description: '',
      cost: 0,
      carts: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:3000/item/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            carts: response.data.map(item => item.itemName),
            cartName: response.data[0].itemName
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeCartName(e){
    this.setState({
      cartName: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const item = {
      cartName: this.state.cartName,
      description: this.state.description,
      cost: this.state.cost
    }

    console.log(item);

    axios.post('http://localhost:3000/cart/add', item)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Purchase Order Adding to Cart</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Item Name: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.cartName}
              onChange={this.onChangeCartName}>
              {
                this.state.carts.map(function(cartName) {
                  return <option 
                    key={cartName}
                    value={cartName}>{cartName}
                    </option>;
                })
              }
          </select>
        </div>

        <div className="form-group">
          <input type="submit" value="Add to Cart" className="btn btn-primary" />
        </div>
        <div className="form-group">
        <Link to="/complete" className="btn btn-primary">Submit</Link>
        </div>
      </form>
    </div>
    )
  }
}
