import React, { Component } from 'react';
import axios from 'axios';

export default class CreatePurchase extends Component {
  constructor(props) {
    super(props);

    this.onChangeName = this.onChangeName.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      name: '',
      users: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:3000/orders/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(item => item.name),
            name: response.data[0].name
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeName(e) {
    this.setState({
      username: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const item = {
      name: this.state.name
    }

    console.log(item);

    axios.post('http://localhost:3000/cart/add', item)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Purchase order</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Username: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.name}
              onChange={this.onChangeName}>
              {
                this.state.name.map(function(name) {
                  return <option 
                    key={name}
                    value={name}>{name}
                    </option>;
                })
              }
          </select>
        </div>

        <div className="form-group">
          <input type="submit" value="Create Purchase Order" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
