import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class CreatePurchase extends Component {
  constructor(props) {
    super(props);

    this.onChangeCartName = this.onChangeCartName.bind(this);
    // this.onChangeDescription = this.onChangeDescription.bind(this);
    // this.onChangeCost = this.onChangeCost(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      itemName: '',
      description: '',
      cost: 0,
      items: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:3000/item/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            items: response.data.map(item => item.itemName),
            itemName: response.data[0].itemName
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeCartName(e, item){
    this.setState({
      cartName: e.target.value
    })
  }

  onChangeDescription(e) {
     this.setState({
       description: e.target.value
     })
   }

  // onChangeCost(e) {
  //    this.setState({
  //      cost: e.target.value
  //    })
  //  }

  onSubmit(e) {
    e.preventDefault();

    //this.state.description = items[key].description;

    const cart = {
      cartName: this.state.itemName,
      description: this.state.description,
      cost: this.state.cost
    }


    console.log(cart);
    console.log("attempting to add items");

    axios.post('http://localhost:3000/cart/add', cart)
      .then(res => console.log(res.data));

  }

  render() {
    return (
    <div>
      <h3>Purchase Order Adding to Cart</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Item Name: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.cartName}
              onChange={this.onChangeCartName}>
              {
                this.state.items.map(function(item) {
                  return <option key={item} value={item}>
                    {item}
                  </option>;
                })
              }
          </select>
        </div>

        {/* <div className="form-group"> 
          <label>Pick Description: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}>
              {this.state.items.map(description => (
                  <option key={description.key} value={description.value}>
                    {description.display}
                  </option>
                ))
              }
          </select>
        </div> */}

        {/* <div className="form-group"> 
          <label>Cost: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.cost}
              onChange={this.onChangeCost}>
              {
                this.state.items.map(function(cost) {
                  return <option key={cost} value={cost}>
                    {cost}
                  </option>;
                })
              }
          </select>
        </div> */}

        <div className="form-group">
          <input type="submit" value="Add to Cart" className="btn btn-primary" />
          <Link to="/item" className="btn btn-primary">Item List</Link>
        </div>
        <div className="form-group">
        </div>
      </form>
    </div>
    )
  }
}
