import React, { Component} from 'react';
import axios from 'axios';

export default class Complete extends Component {

  onSubmit(e){
    e.preventDefault();

    axios.delete('http://localhost:3000/cart/')
      .then(response => { console.log("Everything is deleted")});

    window.location = '/';
  }

  render() {
    return (
      <div>
        <h1>Payment Completed</h1>
        <h5 style = {{paddingBottom: '25px'}}>Your Payment has been processed! Press the button below to shop again!</h5>
        <div className="form-group">
          <input type="submit" value="Home" className="btn btn-primary" />
        </div>
      </div>
    )
  }
}
