import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class CreatePurchase extends Component {
  constructor(props) {
    super(props);

    this.onChangeCartName = this.onChangeCartName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeCost = this.onChangeCost(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      cartName: '',
      description: '',
      cost: 0,
      cartss: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:3000/item/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            items: response.data.map(item => item.itemName),
            itemName: response.data[0].itemName
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeCartName(e){
    this.setState({
      cartName: e.target.value
    })
  }

  // onChangeDescription(e) {
  //   this.setState({
  //     description: e.target.value
  //   })
  // }

  // onChangeCost(e) {
  //   this.setState({
  //     cost: e.target.value
  //   })
  // }

  onSubmit(e) {
    e.preventDefault();

    const cart = {
      cartName: this.state.cartName,
      description: this.state.description,
      cost: this.state.cost
    }

    console.log(cart);
    console.log("attempting to add items");

    axios.post('http://localhost:3000/cart/add', cart)
      .then(res => console.log(res.data));

  }

  render() {
    return (
    <div>
      <h3>Purchase Order Adding to Cart</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Item Name: </label>
          <select ref="userInput"
              required
              className="form-control"
              value={this.state.itemName}
              onChange={this.onChangeItemName}>
              {
                this.state.items.map(function(item) {
                  return <option 
                    key={item}
                    value={item}>{item}
                    </option>;
                })
              }
          </select>
        </div>

        <div className="form-group">
          <input type="submit" value="Add to Cart" className="btn btn-primary" />
          <Link to="/item" className="btn btn-primary">Item List</Link>
        </div>
        <div className="form-group">
        </div>
      </form>
    </div>
    )
  }
}
