/*
Creates an item for the whole list
*/

import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class AddItem extends Component {
  constructor(props) {
    super(props);

    this.onChangeItemName = this.onChangeItemName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeCost = this.onChangeCost.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      itemName: '',
      description: '',
      cost: 0,
      itemNames: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:3000/item')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            itemNames: response.data.map(item => item.itemName),
            itemName: response.data[0].itemName
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeItemName(e) {
    this.setState({
      itemName: e.target.value
    })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })
  }

  onChangeCost(e) {
    this.setState({
      cost: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const item = {
      itemName: this.state.itemName,
      description: this.state.description,
      cost: this.state.cost
    }

    console.log(item);

    axios.post('http://localhost:3000/item/add', item)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Create a new purchase order</h3>
      <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Name: </label>
          <select ref="itemNameInput"
              required
              className="form-control"
              value={this.state.itemName}
              onChange={this.onChangeItemName}>
              {
                this.state.itemNames.map(function(itemName) {
                  return <option 
                    key={itemName}
                    value={itemName}>{itemName}
                    </option>;
                })
              }
          </select>
        </div>
        <div className="form-group"> 
          <label>Description: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
              />
        </div>
        <div className="form-group">
          <label>Cost: </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.duration}
              onChange={this.onChangeCost}
              />
        </div>

        <div className="form-group">
          <input type="submit" value="Add Item" className="btn btn-primary" />
        </div>

        <div className="form-group">
        <Link to="/Complete" className="btn btn-primary">Submit</Link>
        </div>
      </form>
    </div>
    )
  }
}
