import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default class CreatePurchase extends Component {
  constructor(props) {
    super(props);

    this.onChangeCartName = this.onChangeCartName.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeCost = this.onChangeCost(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      cartName: '',
      description: '',
      cost: 0,
      items: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:3000/item/')
      .then(response => {
        this.setState({ items: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeCartName(e){
    this.setState({
      cartName: e.target.value
    })
  }

  onChangeDescription(e) {
     this.setState({
       description: e.target.value
     })
   }

  onChangeCost(e) {
     this.setState({
       cost: e.target.value
     })
   }

  onSubmit(e) {
    e.preventDefault();
    const cart = {
      cartName: this.state.cartName,
      description: this.state.description,
      cost: this.state.cost
    }
    console.log(cart);
    console.log("attempting to add items");

    axios.post('http://localhost:3000/cart/add', cart)
      .then(res => console.log(res.data));
  }

  itemList() {
    return this.state.items.map(currentitem => {
      return <Item item={currentitem}/>;
    })
  }



  render() {
    return (
    <div>
      <h3>Purchase Order Adding to Cart</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Cost</th>
            </tr>
          </thead>
          <tbody>
            { this.itemList() }
          </tbody>
        </table>

        <form onSubmit={this.onSubmit}>
        <div className="form-group"> 
          <label>Name: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.cartName}
              onChange={this.onChangeCartName}
              />
        </div>
        <div className="form-group"> 
          <label>Description: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.description}
              onChange={this.onChangeDescription}
              />
        </div>
        <div className="form-group">
          <label>Cost: </label>
          <input 
              type="number" 
              required
              className="form-control"
              value={this.state.cost}
              onChange={this.onChangeCost}
              />
        </div>

        <div className="form-group">
          <input type="submit" value="Add Item" className="btn btn-primary" />
        </div>
        </form>
      </div>
    )}}



